(267, 'wpforms_notifications', 'a:4:{s:6:"update";i:1728707447;s:4:"feed";a:0:{}s:6:"events";a:5:{s:15:"welcome-message";a:6:{s:2:"id";s:15:"welcome-message";s:5:"title";s:19:"Welcome to WPForms!";s:7:"content";s:233:"We’re grateful that you chose WPForms for your website! Now that you’ve installed the plugin, you’re less than 5 minutes away from publishing your first form. To make it easy, we’ve got 600+ form templates to get you started!";s:4:"btns";a:2:{s:4:"main";a:2:{s:3:"url";s:62:"https://www.eco-eye.cn/wp-admin/admin.php?page=wpforms-builder";s:4:"text";s:14:"Start Building";}s:3:"alt";a:2:{s:3:"url";s:163:"https://wpforms.com/docs/creating-first-form/?utm_source=WordPress&utm_medium=Event%20Notification&utm_campaign=liteplugin&utm_content=Welcome%20Read%20the%20Guide";s:4:"text";s:14:"Read the Guide";}}s:4:"type";a:7:{i:0;s:4:"lite";i:1;s:5:"basic";i:2;s:4:"plus";i:3;s:3:"pro";i:4;s:6:"agency";i:5;s:5:"elite";i:6;s:8:"ultimate";}s:5:"start";s:19:"2023-04-12 06:49:33";}s:22:"wp-mail-smtp-education";a:5:{s:2:"id";s:22:"wp-mail-smtp-education";s:5:"title";s:43:"Don’t Miss Your Form Notification Emails!";s:7:"content";s:260:"Did you know that many WordPress sites are not properly configured to send emails? With the free WP Mail SMTP plugin, you can easily optimize your site to send emails, avoid the spam folder, and make sure your emails land in the recipient’s inbox every time.";s:4:"btns";a:2:{s:4:"main";a:2:{s:3:"url";s:59:"https://www.eco-eye.cn/wp-admin/admin.php?page=wpforms-smtp";s:4:"text";s:12:"现在安装";}s:3:"alt";a:2:{s:3:"url";s:195:"https://wpforms.com/docs/how-to-set-up-smtp-using-the-wp-mail-smtp-plugin/?utm_source=WordPress&utm_medium=Event%20Notification&utm_campaign=liteplugin&utm_content=WP%20Mail%20SMTP%20Learn%20More";s:4:"text";s:12:"了解更多";}}s:5:"start";s:19:"2023-04-15 06:49:33";}s:15:"join-vip-circle";a:5:{s:2:"id";s:15:"join-vip-circle";s:5:"title";s:27:"Want to Be a VIP? Join Now!";s:7:"content";s:326:"Running a WordPress site can be challenging. But help is just around the corner! Our Facebook group contains tons of tips and help to get your business growing! When you join our VIP Circle, you’ll get instant access to tips, tricks, and answers from a community of loyal WPForms users. Best of all, membership is 100% free!";s:4:"btns";a:1:{s:4:"main";a:2:{s:3:"url";s:43:"https://www.facebook.com/groups/wpformsvip/";s:4:"text";s:8:"Join Now";}}s:5:"start";s:19:"2023-05-12 06:49:33";}s:14:"survey-reports";a:5:{s:2:"id";s:14:"survey-reports";s:5:"title";s:46:"Want to Know What Your Customers Really Think?";s:7:"content";s:334:"Nothing beats real feedback from your customers and visitors. That’s why many small businesses love our awesome Surveys and Polls addon. Instantly unlock full survey reporting right in your WordPress dashboard. And don’t forget: building a survey is easy with our pre-made templates, so you could get started within a few minutes!";s:4:"btns";a:2:{s:4:"main";a:1:{s:7:"license";a:5:{s:4:"lite";a:2:{s:3:"url";s:178:"https://wpforms.com/lite-upgrade/?utm_source=WordPress&utm_medium=Event%20Notification&utm_campaign=liteplugin&utm_content=Surveys%20and%20Polls%20Upgrade%20Lite&utm_locale=zh_CN";s:4:"text";s:12:"现在升级";}s:5:"basic";a:2:{s:3:"url";s:166:"https://wpforms.com/account/licenses/?utm_source=WordPress&utm_medium=Event%20Notification&utm_campaign=liteplugin&utm_content=Surveys%20and%20Polls%20Upgrade%20Basic";s:4:"text";s:12:"现在升级";}s:4:"plus";a:2:{s:3:"url";s:166:"https://wpforms.com/account/licenses/?utm_source=WordPress&utm_medium=Event%20Notification&utm_campaign=liteplugin&utm_content=Surveys%20and%20Polls%20Upgrade%20Basic";s:4:"text";s:12:"现在升级";}s:3:"pro";a:2:{s:3:"url";s:61:"https://www.eco-eye.cn/wp-admin/admin.php?page=wpforms-addons";s:4:"text";s:12:"现在安装";}s:5:"elite";a:2:{s:3:"url";s:61:"https://www.eco-eye.cn/wp-admin/admin.php?page=wpforms-addons";s:4:"text";s:12:"现在安装";}}}s:3:"alt";a:2:{s:3:"url";s:202:"https://wpforms.com/docs/how-to-install-and-use-the-surveys-an